$(document).ready(function(){

    /**
     * 规格选择价格改变和规格id定义
     */
    $(document).on('click','[data-widget-spec]>.weui-btn',function(){
        var _specId = $(this).data('spec-id'),_price = $(this).data('price');
        $('#spec').attr('value',_specId);
        $(this).addClass('weui-btn_primary').siblings('[data-widget-spec]>.weui-btn').removeClass('weui-btn_primary');
        $('#price-area').html('￥'+_price+'元');
        $('#price').attr('value',_price)
    })
    $(document).on('click','.order-status>.weui-navbar__item',function(){
        $(this).addClass('weui-bar__item_on').siblings('.order-status>.weui-navbar__item').removeClass('weui-bar__item_on');
        var orderStatus = $(this).data('status');
        $.get("/mshop/api/order_list.html",{status:orderStatus},function(res){
            if(res.code == 200){

            }
        })
    })
})
/**
 * 购买数量控制
 * @param e
 */
function addLessNum(e){
    var _idNum = $('#num');
    var num = _idNum.val();
    if(e =='add'){
        _idNum.attr('value',Number(num)+1);
    }else {
        _idNum.attr('value',Number(num)-1);
    }
}

